import React, { useContext } from "react";
import validate from './validation';
import useForm from './useForm';
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";


export function SignupForm(props, { submitForm }) {
  const { switchToSignin } = useContext(AccountContext);
  const { handleChange, handleSubmit, values, errors } = useForm(
    submitForm,
    validate
  );


  return (
    <BoxContainer>
      <FormContainer onSubmit={handleSubmit} noValidate>
        <div><Input type="text" placeholder="Full Name" name='username' value={values.username} onChange={handleChange} />
          {errors.username && <p>{errors.username}</p>}</div>
        <div><Input type="email" placeholder="Email" name='email' value={values.email} onChange={handleChange} />
          {errors.email && <p>{errors.email}</p>}</div>
        <div><Input type="password" placeholder="Password" name='password' value={values.password} onChange={handleChange} />
          {errors.password && <p>{errors.password}</p>}</div>
        <div><Input type="password" placeholder="Confirm Password" name='password2' value={values.password2} onChange={handleChange} />
          {errors.password2 && <p>{errors.password2}</p>}</div>

        <Marginer direction="vertical" margin={10} />
        <SubmitButton type="submit">Signup</SubmitButton>

        <Marginer direction="vertical" margin="1em" />
        <MutedLink href="#">
          Already have an account?
          <BoldLink href="#" onClick={switchToSignin}>
            Signin
          </BoldLink>
        </MutedLink>
      </FormContainer>
    </BoxContainer>
  );
}
