import React, { useState, useEffect } from "react";
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import { Editor } from "react-draft-wysiwyg";
import { EditorState, convertToRaw, ContentState, convertFromRaw } from "draft-js";
import { stateToHTML } from "draft-js-export-html";
import DatePicker from "react-datepicker";
import { useParams } from 'react-router-dom';
import axios from "axios";
import moment from "moment";
import "react-datepicker/dist/react-datepicker.css";
import "./textEditor.css";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import validate from "../accountBox/validateInfoPost";
import useForm3 from "../accountBox/useForm3";



const EditPost = (submitForm) => {
  const { errors } = useForm3(
    submitForm,
    validate
  );
  let { id } = useParams();
  const [postObject, setPostObject] = useState({});

  const [topic, setTopic] = useState("");
  const [username, setUsername] = useState("");
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  const [startDate, setStartDate] = useState(new Date());
  const [postText, setPostText] = useState("");
  // const postText = stateToHTML(editorState.getCurrentContent());

  useEffect(() => {
    axios.get(`http://localhost:5000/posts/byId/${id}`).then((response) => {
      setTopic(response.data.topic);
      setUsername(response.data.username);
      setTitle(response.data.title);
      setLocation(response.data.location);
      setPostText(response.data.postText);
      setEditorState(EditorState.createWithContent(ContentState.createFromText(response.data.postText)));
      setPostObject(response.data);
    });

  }, []);



  const handleTime = (date) => {
    setStartDate(date);
  };

  const onEditorStateChange = (editorState) => {
    setEditorState(editorState);

  };

  const editPost = (option) => {
    // if (Object.keys(errors).length === 0) {
    //   axios.put(`http://localhost:5000/posts/${id}`, {
    //     topic: topic,
    //     startDate: startDate,
    //     location: location,
    //     username: username,
    //     title: title,
    //     postText: postText
    //   }, {
    //     headers: { accessToken: localStorage.getItem("accessToken") },
    //   }).then((response) => {
    //     window.location.href = `/post/${id}`;
    //   });


    // }
    if (option === "title") {
      let newTitle = prompt("Enter New Title:");
      axios.put('http://localhost:5000/posts/title', {
          
        title: title,
        id: id,
      }, {
        headers: { accessToken: localStorage.getItem("accessToken") },
      });
      setPostObject({ ...postObject, title: newTitle });

    }
  };
  const uploadCallback = (file) => {
    return new Promise(
      (resolve, reject) => {
        if (file) {
          let reader = new FileReader();
          reader.onload = (e) => {
            resolve({ data: { link: e.target.result } })
          };
          reader.readAsDataURL(file);
        }
      }
    );
  }

  return (
    <div>
      <form id='postForm' onSubmit={editPost}>
        <h1 className='postPageTitle'>Create Your Own Campaign !</h1>
        <div className='selectInfo'>
          <div className='selectTopic'>
            <label>Select One Topic:</label>

            <select id='topicSelection' className='topic' name='topic' value={topic} onChange={(e) => {
              setTopic(e.target.value)
            }}>
              <option value='none' hidden>Select a topic</option>
              <option value='sports'>Sports</option>
              <option value='Marketing'>Marketing</option>
              <option value='education'>Education</option>
              <option value='society'>Society</option>
              <option value='other'>Other</option>
            </select>
            {errors.topic && <p className='errors'>{errors.topic}</p>}
          </div>
          <div className='selectTime'>
            <label>Select Date and Time:</label>

            <DatePicker className='datepick'
              dateFormat="yyyy-MM-dd HH:mm"
              timeFormat="hh:mm"
              selected={startDate}
              name='startDate'
              value={moment(startDate).format("DD-MM-YYYY HH:mm")}
              onChange={handleTime}
              showTimeSelect
            />
            {errors.startDate && <p className='errors'>{errors.startDate}</p>}
          </div>

        </div>
        <div className="otherInfo">
          <div className="campaignLocation">
            <label>Location:</label>

            <input className='locationInput' type="text" name='location' value={location} onChange={(e) => {
              setLocation(e.target.value)
            }} />
            {errors.location && <p className='errors'>{errors.location}</p>}
          </div>
          <div className="authorName">
            <label>Author:</label>

            <input type="text" className="author" name='username' value={username} onChange={(e) => {
              setUsername(e.target.value)
            }} />
            {errors.username && <p className='errors'>{errors.username}</p>}
          </div>
        </div>

        <div className="postTitle">
          <label>Title:</label>

          <input type="text" className="titleInput" name='title' value={title}   onClick={() => {
              if (authState.username === postObject.username) {
                editPost("title");
              }
            }}onChange={(e) => setTitle(e.target.value)} />
          {errors.title && <p className='errors'>{errors.title}</p>}
        </div>

        <Editor
          name='postText'
          value={postText}
          editorState={editorState}
          toolbarClassName="toolbarClassName"
          wrapperClassName="wrapperClassName"
          editorClassName="editorClassName"
          onEditorStateChange={onEditorStateChange}
          toolbar={{
            inline: { inDropdown: true },
            list: { inDropdown: true },
            textAlign: { inDropdown: true },
            link: { inDropdown: true },
            history: { inDropdown: true },
            image: {
              uploadEnabled: true,
              uploadCallback: uploadCallback,
              previewImage: true,
              inputAccept: 'image/gif,image/jpeg,image/jpg,image/png,image/svg',
              alt: { present: false, mandatory: false },
              defaultSize: {
                height: 'auto',
                width: 'auto',
              },
            },
          }}
        />
        {errors.postText && <p className='errors-content'>{errors.postText}</p>}
        <div className='postBtnContainer'>
          <button className='postButton' type='submit'>
            Update
          </button>
        </div>
      </form>
    </div>
  );

}
export default EditPost;
