import React, { useContext } from "react";
import validate from './validation';
import useForm from './useForm';
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";


export function SignupForm(props, {submitForm}) {
  const { switchToSignin } = useContext(AccountContext);
    const { handleChange, handleSubmit, values, errors } = useForm(
      submitForm,
      validate
    );


    return (
      <BoxContainer>
        <FormContainer onSubmit={handleSubmit} noValidate>
          <Input type="text" placeholder="Full Name" value={values.username} onChange={handleChange} />
            {errors.username && <p>{errors.username}</p>}
          <Input type="email" placeholder="Email" value={values.email} onChange={handleChange} />
            {errors.email && <p>{errors.email}</p>}
          <Input type="password" placeholder="Password" value={values.password} onChange={handleChange} />
            {errors.password && <p>{errors.password}</p>}
          <Input type="password" placeholder="Confirm Password" />
        </FormContainer>
        <Marginer direction="vertical" margin={10} />
        <SubmitButton type="submit">Signup</SubmitButton>
        <Marginer direction="vertical" margin="1em" />
        <MutedLink href="#">
          Already have an account?
          <BoldLink href="#" onClick={switchToSignin}>
            Signin
          </BoldLink>
        </MutedLink>
      </BoxContainer>
    );
}
