import React, { useEffect, useState } from "react";
import '../../App.css';
import '../Cards.css';
import CardItem from '../CardItem';
import moment from "moment";
import axios from "axios";


export default function Campaigns() {
  const [listOfPosts, setListOfPosts] = useState([]);
  const [signedPosts, setSignedPosts] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      window.location.href = '/login';
    } else {
      axios.get("http://localhost:5000/posts", {
      headers: { accessToken: localStorage.getItem("accessToken") },
    }).then((response) => {
      setListOfPosts(response.data.listOfPosts);
      setSignedPosts(
        response.data.signedPosts.map((sign) => {
          return sign.PostId
        })
      )

    })
    }

  }, []);

  const signAPost = (postId) => {
    axios.post("http://localhost:5000/signature", {
      PostId: postId
    },
      { headers: { accessToken: localStorage.getItem("accessToken") } }
    ).then((response) => {
      setListOfPosts(listOfPosts.map((post) => {
        if (post.id === postId) {
          if (response.data.signed) {
            return { ...post, Signature: [...post.Signature, 0] };
          } else {
            const signatureArray = post.Signature;
            signatureArray.pop();
            return { ...post, Signature: signatureArray };
          }
        } else {
          return post;
        }
      }));
      if (signedPosts.includes(postId)) {
        setSignedPosts(
          signedPosts.filter((id) => {
            return id != postId;
          })
        );
      } else {
        setSignedPosts([...signedPosts, postId]);
      }
    });
  };

  return (
    <>
      <div className='campaigns'>
        <div className='overlay'>
          <h1 className='campaignTitle'>Campaigns</h1>
        </div>
      </div>

      <div className='cards'>
        <div className='cards__container'>
          <div className='cards__wrapper' >

            <ul className='cards__items_campaign'>
              {listOfPosts.map((value, key) => {
                return (
                  <>
                    <CardItem key={key}
                      src='images/img-9.jpg'
                      title={value.title}
                      text={value.postText}
                      topic={value.topic}
                      username={value.username}
                      dateTime={moment(value.createdAt).format("DD-MM-YYYY HH:mm:ss")}
                      path={`/post/${value.id}`}
                    />
                    <button className='btnSign' onClick={() => {
                      signAPost(value.id);
                    }}>Sign</button>
                    <label>{ value.Signature.length}</label>
                  </>
                )
              })}
            </ul>
          </div>
        </div>
      </div>
    </>
  )
}
