import { useState, useEffect } from 'react';
import { Editor } from "react-draft-wysiwyg";
import { EditorState } from "draft-js";
import { stateToHTML } from "draft-js-export-html";
import Axios from 'axios';

const useForm = (callback, validate) => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  const [startDate, setStartDate] = useState(new Date());

  const [values, setValues] = useState({
    topic: '',
    username: '',
    title: '',
    postText: stateToHTML(editorState.getCurrentContent()),
    location: ''

  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  Axios.defaults.withCredentials = true;

  const handleChange = e => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value
    });

  };

  const handleTime = (date) => {
    setStartDate(date);
  }

  const handleSubmit = e => {
    e.preventDefault();


    setErrors(validate(values));
    setIsSubmitting(true);


     Axios.post('http://localhost:5000/posts', {topic:values.topic, username:values.username, title: values.title, postText:values.postText}).then((response) => {
       console.log("it worked");
     });
    // document.getElementById("postForm").reset();

  };

  const onEditorStateChange = (editorState) => {
    setEditorState(editorState)
  }


  useEffect(
    () => {
      if (Object.keys(errors).length === 0 && isSubmitting) {
        callback();

        // Axios.post('http://localhost:5000/register', {
        //   signupName: values.username, email: values.email, password: values.password
        // });

      }
    },
    [errors]
  );

  return { handleChange, handleSubmit, onEditorStateChange, handleTime, startDate, editorState, values, errors };
};

export default useForm;
