import React, { useState } from 'react';
import './Form.css';
import FormSignup from './FormSignup';
import FormSuccess from './FormSuccess';
import Axios from 'axios';

const Form = () => {
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [values, setValues] = useState({
    username: '',
    email: '',
    password: '',
    password2: ''
  });


  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = e => {
    const { name, value } = e.target;

    setValues({
      ...values,
      [name]: value
    });

  };

  const handleSubmit = e => {
    e.preventDefault();


    setErrors(validate(values));
    setIsSubmitting(true);



  };

  if (isSubmitted) {
    Axios.post('http://localhost:5000/api/create', {
      signupName: values.username, email: values.email, password: values.password, confirmPassword: values.password2
    });
  }
  function submitForm() {
    setIsSubmitted(true);

  }
  return (
    <>
      <div className='form-container'>
        <span className='close-btn'>Ã—</span>
        <div className='form-content-left'>
          <img className='form-img' src='images/img-2.svg' alt='spaceship' />
        </div>
        {!isSubmitted ? (
          <FormSignup submitForm={submitForm} />
        ) : (
          <FormSuccess />
        )}
      </div>
    </>
  );
};

export default Form;
