import React, { useEffect, useState } from "react";
import '../../App.css';
import '../Cards.css';
import CardItem from '../CardItem';
import axios from "axios";


export default function Campaigns() {
  const [listOfPosts, setListOfPosts] = useState([]);
  const [signedPosts, setSignedPosts] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/posts").then((response) => {
      setListOfPosts(response.data);


    })
  }, []);

  const signAPost = (postId) => {
    axios.post("http://localhost:5000/signature", {
      PostId: postId
    },
      { headers: { accessToken: localStorage.getItem("accessToken") } }
    ).then((response) => {
      setListOfPosts(listOfPosts.map((post) => {
        if (post.id === postId) {
          if (response.data.signed) {
            return { ...post, Signature: [...post.Signature, 0] };
          } else {
            const signatureArray = post.Signature;
            signatureArray.pop();
            return { ...post, Signature: signatureArray };
          }
        } else {
          return post;
        }
      }));
      if (signedPosts.includes(postId)) {
        setSignedPosts(
          signedPosts.filter((id) => {
            return id != postId;
          })
        );
      } else {
        setSignedPosts
      }
    })
  };

  return (
    <>
      <div className='campaigns'>
        <div className='overlay'>
        <h1 className='campaignTitle'>Campaigns</h1>
        </div>     
      </div>

      <div className='cards'>
        <div className='cards__container'>
          <div className='cards__wrapper' >

            <ul className='cards__items_campaign'>
              {listOfPosts.map((value, key) => {
                return (

                  <CardItem key={key}
                    src='images/img-9.jpg'
                    title={value.title}
                    text={value.postText}
                    topic={value.username}
                    path={`/post/${value.id}`}
                  />

                )
              })}
            </ul>
          </div>
        </div>
      </div>
    </>
  )
}
