import React from 'react';
import './Cards.css';
import CardItem from './CardItem';
import axios from "axios";
import { useEffect, useState } from "react";


function Cards() {
  const [listOfPosts, setListOfPosts] = useState([]);
  const [signedPosts, setSignedPosts] = useState([]);


  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      window.location.href = '/login';
    } else {
      axios.get("http://localhost:5000/posts", {
      headers: { accessToken: localStorage.getItem("accessToken") },
    }).then((response) => {
      setListOfPosts(response.data.listOfPosts);
      setSignedPosts(
        response.data.signedPosts.map((sign) => {
          return sign.PostId
        })
      )

    })
    }

  }, []);

  return (
    <div className='cards'>
      <h1>Check out these fantastic Campaigns!</h1>
      <div className='cards__container'>
        <div className='cards__wrapper' >

          <ul className='cards__items'>
            {listOfPosts.slice(listOfPosts.length - 2, listOfPosts.length).reverse().map((value, key) => {
              return (

                <CardItem key={key}
                  src='images/img-9.jpg'
                  title={value.title}
                  text={value.postText}
                  topic={value.username}
                  path={`/post/${value.id}`}
                />

              )
            })}
          </ul>
          <ul className='cards__items'>
            {listOfPosts.slice(listOfPosts.length - 5, listOfPosts.length - 2).reverse().map((value, key) => {
              return (

                <CardItem key={key}
                  src='images/img-9.jpg'
                  title={value.title}
                  text={value.postText}
                  topic={value.username}
                  path={`/post/${value.id}`}
                />

              )
            })}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Cards;
