import React from 'react';
import { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import './App.css';
import Home from './components/pages/Home';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Campaigns from './components/pages/Campaigns';
import Products from './components/pages/Products';
import SignUp from './components/pages/SignUp';
import Login from './components/pages/Login';
import Post from './components/pages/Post';
import useForm2 from './components/accountBox/useForm2';



function App(submitForm) {
  const {userLoggedIn} = useForm2(submitForm)
  const [user, setUser] = useState(userLoggedIn);
  useEffect(() => {
    setUser
  });


  return (
    <>
      <Router>
        <Navbar />
        <Switch>
          <Route path='/' exact component={Home} />
          <Route path='/campaigns' component={Campaigns} />
          <Route path='/products'>{user ? <Products /> : <Login />}</Route>
          <Route path='/post'>{user ? <Post /> : <Login />}</Route>
          <Route path='/sign-up'>{user ? <Home /> : <SignUp />}</Route>
          <Route path='/login'> {user ? <Home /> : <Login />}</Route>
        </Switch>
      </Router>
    </>
  );
}

export default App;
