import React from 'react';
import { useState, useEffect } from 'react';
import './App.css';
import Home from './components/pages/Home';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Navbar from './components/Navbar';
import Campaigns from './components/pages/Campaigns';
import Profile from './components/pages/Profile';
import SignUp from './components/pages/SignUp';
import Login from './components/pages/Login';
import Write from './components/pages/Write';
import Post from './components/pages/Post';
import sucessForm from './components/accountBox/FormSuccess';
import { AuthContext } from "./Context/AuthContext";
import './components/Navbar.css';
import { Button } from './components/Button';
import axios from 'axios';


function App() {

  const [authState, setAuthState] = useState({
    username: "",
    id: 0,
    status: false,
  });
  // const [click, setClick] = useState(false);
  // const [button, setButton] = useState(true);

  // const handleClick = () => setClick(!click);
  // const closeMobileMenu = () => setClick(false);
  // const showButton = () => {
  //   if (window.innerWidth <= 960) {
  //     setButton(false);
  //   } else {
  //     setButton(true);
  //   }
  // };

  useEffect(() => {
    // showButton();

    axios.get("http://localhost:5000/auth/verification", {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      }
    }).then((response) => {
      if (response.data.error) {
        setAuthState({ ...authState, status: false });
      } else {
        setAuthState({
          username: response.data.username,
          id: response.data.id,
          status: true,
        });  
      }
    });

  }, []);

  // const logout = (e) => {
  //   e.preventDefault();
  //   localStorage.removeItem("accessToken");
  //   setAuthState({ username: "", id: 0, status: false });
  //   window.location.href = '/';
  // };

  // window.addEventListener('resize', showButton);
  return (
    <>
      <AuthContext.Provider value={{ authState, setAuthState }}>
        <Router>
          <Navbar />
          {/* <nav className='navbar'>
            <div className='navbar-container'>
              <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
                Campaign <img className='img-logo' src="images/logo.png" alt="" width="70" height="50" /> with Us

              </Link>
              <div className='menu-icon' onClick={handleClick} >
                <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
              </div>
              <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                <li className='nav-item'>
                  <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                    Home
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to='/campaigns'
                    className='nav-links'
                    onClick={closeMobileMenu}
                  >
                    Campaigns
                  </Link>
                </li>

                {authState.status ?
                  (<li className='nav-item'>
                    <Link
                      to='/write'
                      className='nav-links'
                      onClick={closeMobileMenu}
                    >Post</Link>
                  </li>) : (<li className='nav-item'>
                    <Link
                      to='/login'
                      className='nav-links'
                      onClick={closeMobileMenu}
                    >Post</Link>
                  </li>)}

                {authState.status &&
                  <li className='nav-item'>
                    <Link
                      to='/my-profile'
                      className='nav-links'
                      onClick={closeMobileMenu}
                    >
                      My Profile
                    </Link>
                  </li>
                }
                {!authState.status ?
                  (<li>
                    <Link
                      to='/login'
                      className='nav-links-mobile'
                      onClick={closeMobileMenu}
                    >
                      Login/Signup
                    </Link>
                  </li>) : (<li>
                    <Link
                      to='/'
                      className='nav-links-mobile'
                      onClick={logout}
                    >
                      Logout
                    </Link>
                  </li>)}
              </ul>
              {!authState.status ? (<div>{button && (<Button buttonStyle='btn--signup'>Login/Signup</Button>)}</div>) : (<div><span className='userNav'>{authState.username}</span>{button && (<Button onClick={logout} buttonStyle='btn--signup'>Logout</Button>)}</div>)}
            </div>
          </nav> */}
          <Switch>
            {!authState ?(
              <>
              <Route path='/sign-up' exact component={SignUp} />
            <Route path='/login' exact component={Login} />
            <Route path='/success-form' exact component={sucessForm} />
              </>
            ) : (
                <></>
            )
          }
            <Route path='/write' component={Write} />{authState ? <Write /> : <Login />}
            <Route path='/post/:id' exact component={Post} />
            <Route path='/sign-up' exact component={SignUp} />
            <Route path='/login' exact component={Login} />
            <Route path='/success-form' exact component={sucessForm} />
          </Switch>
        </Router>
      </AuthContext.Provider>
    </>
  );
}

export default App;
