import React, { useState } from "react";
import { Editor } from "react-draft-wysiwyg";
import { EditorState } from "draft-js";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./textEditor.css";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import Axios from 'axios';


const TextEditor = () => {
  const [userName, setUserName] = useState("");
  const [topic, setTopic] = useState("");
  const [title, setTitle] = useState("");
  const [editorState, setEditorState] = useState(EditorState.createEmpty())

  const onEditorStateChange = (editorState) => {
    setEditorState(editorState)
  }

  const [location, setLocation] = useState("");
  const [startDate, setStartDate] = useState(new Date());


  const submitPost = () => {
    Axios.post('http://localhost:5000/api/create', {
      userName: userName, topic: topic, title: title, editorState: editorState, location: location, startDate: startDate
    })
  }

  function uploadImageCallBack(file) {
    return new Promise(
      (resolve, reject) => {
        const xhr = new XMLHttpRequest();
        xhr.open('POST', 'https://api.imgur.com/3/image');
        xhr.setRequestHeader('Authorization', 'Client-ID 53a93612d592f17');
        const data = new FormData();
        data.append('image', file);
        xhr.send(data);
        xhr.addEventListener('load', () => {
          const response = JSON.parse(xhr.responseText);
          console.log(response)
          resolve(response);
        });
        xhr.addEventListener('error', () => {
          const error = JSON.parse(xhr.responseText);
          console.log(error)
          reject(error);
        });
      }
    );
  }

  return (
    <div>
      <h1 className='postPageTitle'>Create Your Own Campaign !</h1>
      <div className='selectInfo'>
        <div className='selectTopic'>
          <label>Select One Topic:</label>
          <select className='topic' onChange={(e) => { setTopic(e.target.value) }}>
            <option value='sports'>Sports</option>
            <option value='Marketing'>Marketing</option>
            <option value='education'>Education</option>
            <option value='society'>Society</option>
          </select>
        </div>
        <div className='selectTime'>
          <label>Select Date and Time:</label>
          <DatePicker className='datepick' selected={startDate} onChange={(date) => setStartDate(date)} showTimeSelect
            dateFormat="Pp" />
        </div>
      </div>
      <div className="otherInfo">
        <div className="campaignLocation">
          <label>Location:</label>
          <input className='locationInput' type="text" onChange={(e) => { setLocation(e.target.value) }} />
        </div>
        <div className="authorName">
          <label>Author Name:</label>
          <input type="text" className="author" onChange={(e) => { setUserName(e.target.value) }} />
        </div>
      </div>

      <div className="postTitle">
        <label>Title:</label>
        <input type="text" className="titleInput" onChange={(e) => { setTitle(e.target.value) }} />
      </div>
      <Editor
        editorState={editorState}
        toolbarClassName="toolbarClassName"
        wrapperClassName="wrapperClassName"
        editorClassName="editorClassName"
        onEditorStateChange={onEditorStateChange}
        toolbar={{
          inline: { inDropdown: true },
          list: { inDropdown: true },
          textAlign: { inDropdown: true },
          link: { inDropdown: true },
          history: { inDropdown: true },
          image: { uploadCallback: uploadImageCallBack, alt: { present: true, mandatory: true } },
        }}
      />
      <button className='postButton' type='submit'>
        Post
      </button>
    </div>
  );

}
export default TextEditor;
