import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import useForm2 from './accountBox/useForm2';
import './Navbar.css';
import { AuthContext } from "./Context/AuthContext";
import axios from 'axios';

function Navbar({ submitForm }) {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);
  const [authState, setAuthState] = useState(false);
  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };
  useEffect(() => {
    axios.get('http://localhost:5000/auth/verification', {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      }
    }).then((response) => {
      if (response.data.error) {
        setAuthState(false);
      } else {
        setAuthState(true);
      }
    });
    showButton();
  }, []);







  window.addEventListener('resize', showButton);



  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            Campaign <img className='img-logo' src="images/logo.png" alt="" width="70" height="50" /> with Us

          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/campaigns'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Campaigns
              </Link>
            </li>
            {user&&(<li className='nav-item'>
              <Link
                to='/write'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Post
              </Link>
            </li>)||<Link
                to='/login'
                className='nav-links'
                onClick={closeMobileMenu}
              >Post</Link>}

            {<li></li>||user && (<li className='nav-item'>
              <Link
                to='/products'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                My Account
              </Link>
            </li>)}

            {user &&
              (<li>
                <Link
                  to='/login'
                  className='nav-links-mobile'
                  onClick={closeMobileMenu}
                >
                  Login/Signup
                </Link>
              </li>)
            }

            {!user &&
              (<li>
                <Link
                  to='/'
                  className='nav-links-mobile'
                  onClick={closeMobileMenu}
                >
                  Logout
                </Link>
              </li>)
            }

          </ul>
          {!user && (button && (<Button buttonStyle='btn--signup'>Login/Signup</Button>)) || <Button onClick={ handleLogout } buttonStyle='btn--signup'><span>{loginStatus}</span> Logout</Button>}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
