import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import { AuthContext } from "../Context/AuthContext";
import {NavLink} from 'react-router-dom'
import axios from 'axios';
import './Navbar.css';


function Navbar() {
  const [authState, setAuthState] = useState({
    username: "",
    id: 0,
    status: false,
  });
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);
  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();

    axios.get("http://localhost:5000/auth/verification", {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      }
    }).then((response) => {
      if (response.data.error) {
        setAuthState({ ...authState, status: false });
      } else {
        setAuthState({
          username: response.data.username,
          id: response.data.id,
          status: true,
        });  
      }
    });

  }, []);

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("accessToken");
    setAuthState({ username: "", id: 0, status: false });
    window.location.href = '/';
  };

  window.addEventListener('resize', showButton);

  return (
    <>
      <AuthContext.Provider value={{ authState, setAuthState }}>
          <nav className='navbar'>
            <div className='navbar-container'>
              <NavLink to='/' className='navbar-logo' onClick={closeMobileMenu}>
                Campaign <img className='img-logo' src="images/logo.png" alt="" width="70" height="50" /> with Us

              </NavLink>
              <div className='menu-icon' onClick={handleClick} >
                <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
              </div>
              <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                <li className='nav-item'>
                  <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                    Home
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to='/campaigns'
                    className='nav-links'
                    onClick={closeMobileMenu}
                  >
                    Campaigns
                  </Link>
                </li>

                {authState.status ?
                  (<li className='nav-item'>
                    <Link
                      to='/write'
                      className='nav-links'
                      onClick={closeMobileMenu}
                    >Post</Link>
                  </li>) : (<li className='nav-item'>
                    <Link
                      to='/login'
                      className='nav-links'
                      onClick={closeMobileMenu}
                    >Post</Link>
                  </li>)}

                {authState.status &&
                  <li className='nav-item'>
                    <Link
                      to='/my-profile'
                      className='nav-links'
                      onClick={closeMobileMenu}
                    >
                      My Profile
                    </Link>
                  </li>
                }
                {!authState.status ?
                  (<li>
                    <Link
                      to='/login'
                      className='nav-links-mobile'
                      onClick={closeMobileMenu}
                    >
                      Login/Signup
                    </Link>
                  </li>) : (<li>
                    <Link
                      to='/'
                      className='nav-links-mobile'
                      onClick={logout}
                    >
                      Logout
                    </Link>
                  </li>)}
              </ul>
              {!authState.status ? (<div>{button && (<Button buttonStyle='btn--signup'>Login/Signup</Button>)}</div>) : (<div><span className='userNav'>{authState.username}</span>{button && (<Button onClick={logout} buttonStyle='btn--signup'>Logout</Button>)}</div>)}
            </div>
          </nav>

      </AuthContext.Provider>
    </>
  );
}

export default Navbar;
