import { useState, useEffect } from 'react';
import { Editor } from "react-draft-wysiwyg";
import { EditorState } from "draft-js";
import { stateToHTML } from "draft-js-export-html";
import Axios from 'axios';

const useForm = (callback, validate) => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());


  const [values, setValues] = useState({
    username: '',
    email: '',
    password: '',
    password2: '',
    authorname: '',
    topic: '',
    title: '',
    content: stateToHTML(editorState.getCurrentContent()),
    location: '',
    startData: ''

  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  // Axios.defaults.withCredentials = true;

  const handleChange = e => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value
    });

  };

  const handleSubmit = (e, data) => {



    setErrors(validate(values));
    setIsSubmitting(true);

    Axios.post('http://localhost:5000/posts', data).then((response) => {
      console.log("it worked");
    });
    // document.getElementById("postForm").reset();

  };

  const onEditorStateChange = (editorState) => {
    setEditorState(editorState)
  }


  useEffect(
    () => {
      if (Object.keys(errors).length === 0 && isSubmitting) {
        callback();

        // Axios.post('http://localhost:5000/register', {
        //   signupName: values.username, email: values.email, password: values.password
        // });

      }
    },
    [errors]
  );

  return { handleChange, handleSubmit, onEditorStateChange, editorState, values, errors };
};

export default useForm;
